import './EditElementComponent.css'
import { useEffect, useState } from 'react'
import axios from 'axios';

const EditElementComponent = (props) => {

  const [data, setData] = useState({});
  
  const fetchElementData = (id) => {
    axios.get(`http://localhost:3001/api/get/recipes/${id}`)
      .then( res => {
        setData(res.data);
      })
      .catch( err => {
        console.error(err);
        props.editingState(false);
      })
  }

  useEffect(() => {
    fetchElementData(props.elementid);
  }, [])

  const handleSubmit = (event) => {
    event.preventDefault();

    const ingrArray = event.target.Ingredients.split('\n');
    const stepArray = event.target.Steps.split('\n');
    const fNames = Array.from(event.target.files).map( (file) => file.name );

    setData({
      title: event.target.Title,
      description: event.target.Description,
      ingredients: ingrArray,
      steps: stepArray,
      files: fNames,
      icon: fNames[0]
    })

    console.log(data)
    axios.post(`http://localhost:3001/api/post/update-recipe/${props.elementid}`, data);
    props.editingState(false);
  }

  return (
    <>
      <form className="edit-component" onSubmit={handleSubmit}>
        <span className="text">Edit Recipe</span>
        <input 
          type="text" 
          name="Title" 
          placeholder="Title" 
          className="edit-form input" 
          defaultValue={data.title}/>
        <textarea 
          name="Description" 
          placeholder="Description" 
          className="edit-form input" 
          defaultValue={data.description}/>
        <textarea 
          type="text" 
          name="Ingredients" 
          placeholder="Ingredients" 
          className="edit-form input"
          defaultValue={data.ingredients}/>
        <textarea 
          type="text" 
          name="Steps" 
          placeholder="Steps" 
          className="edit-form input"
          defaultValue={data.steps} />
        <input 
          type="file" 
          name="Files" 
          placeholder="Title" 
          className="edit-form input"
          multiple/>

        <input type="submit" value="Update" className="edit-form submit"></input>

        <input type="button" value="X" className="edit-form close-form" onClick={() => props.editingState(false)}></input>
      </form>
    </>
    )
}

export default EditElementComponent;
