const getCookie = (name) => {
  const cookies = document.cookie.split(';');
  return cookies.find((cookie) => {
    const c = cookie.trim().split('=')
    console.log(c);
    if (c[0] === name) return c[1]
    else return false;
  })
}

export default getCookie;
