import './TopMenu.css';
import Login from '../login.js';
import getCookieValue from '../cookie.js';



const TopMenu = () => {
  const [loging, setLoging] = useState(null)

  const loginForm = () => {
    return (
      <div className="login-form">
        <form onSubmit={Login.login}>
          <input type="text" name="username"></input>
          <input type="password" name="password"></input>
          <input type="submit" value="Login"></input>
        </form>
      </div>
      )
  }

  const loginBarLogic = () => {
    const sessionid_v = getCookieValue('sessionid')
    console.log(sessionid_v);
    const loginButton = <div className="option" onClick={() => setLoging(true)}>Sign In</div>
    const logoutButton = <div className="option" onClick={() => Login.logout()}>LogOut</div>
    if (sessionid_v && sessionid_v !== undefined) {

    } else {
      console.log('login bar')
      return (loginButton);
    }
  }

  return (      
    <div className="menu top">
      <div className="title">
        <span>COOK BOOK</span>
      </div>
    <div className="options">
      { loginBarLogic() }
     </div>
    </div>
  )
}

export default TopMenu;
