const getCookieValue = async (name) => {
  const cookies = document.cookie.split(';');
  const res = await cookies.find((cookie) => {
    const c = cookie.trim().split('=')
    if (c[0] === name) return c[1]
    return undefined;
  })
  console.log(res);
  return res;
}

export default getCookieValue;
