import React, { useState, useEffect } from 'react';
import './TopMenu.css';
import login, {logout, validateSession} from '../login.js';
import getCookieValue from '../cookie.js';

const TopMenu = () => {
  const [loging, setLoging] = useState(false)

  const formSubmitControl = (event) => {
    login(event);
    setLoging(false);
  }
  const [loginBar, setLoginBar] = useState();
  
  const loginBarLogic = () => {
    const sessionid_v = getCookieValue('sessionid')
    const loginButton = (
      <div className="option" onClick={() => setLoging(true)}>
        Sign In
      </div>);
    const logoutButton = (
      <div className="option" onClick={() => logout(sessionid_v)}>
        LogOut
      </div>);
    const loginForm = (
      <div className="login-form">
        <form onSubmit={formSubmitControl}>
          <input type="text" name="username"></input>
          <input type="password" name="password"></input>
          <input type="submit" value="Login"></input>
          <input type="button" onClick={() => setLoging(false)} value="Cancel"></input>
        </form>
      </div>
      )
    if (sessionid_v && sessionid_v !== 'undefined') {
      if (validateSession(sessionid_v)) {
        return setLoginBar(logoutButton);
      } else {
        document.cookie = "";
        return setLoginBar(loging ? loginForm : loginButton);
      }
    } else {
      return setLoginBar(loging ? loginForm : loginButton);
    }
  }


  useEffect(() => {
    // setLoginBar(() => loginBarLogic());
  }, [loginBar, loging])

  return (      
    <div className="menu top">
      <div className="title">
        <span>COOK BOOK</span>
      </div>
    <div className="options">
      { loginBar }
     </div>
    </div>
  )
}

export default TopMenu;
