import React, { useState } from 'react';
import './TopMenu.css';
import {login, validate, logout} from '../login.js';
import getCookieValue from '../cookie.js';



const TopMenu = () => {
  const [loging, setLoging] = useState(null)

  const loginForm = () => {
    return (
      <div className="login-form">
        <form onSubmit={login}>
          <input type="text" name="username"></input>
          <input type="password" name="password"></input>
          <input type="submit" value="Login"></input>
        </form>
      </div>
      )
  }

  return (      
    <div className="menu top">
      <div className="title">
        <span>COOK BOOK</span>
      </div>
    <div className="options">
      { 
      getCookieValue('sessionid') ? 
        validateSession() :
        null
      }
      { !loging ? 
      <div className="option" onClick={() => setLoging(true)}>Sign In</div> : 
      loginForm()
      }
     </div>
    </div>
  )
}

export default TopMenu;
