import React, { useState, useEffect } from 'react';
import './TopMenu.css';
import login, {logout, validateSession} from '../login.js';
import getCookieValue from '../cookie.js';

const TopMenu = () => {
  // Sign In button - 0
  // Sign In form - 1
  // Logout button - 2
  const [loginBarState, setLoginBarState] = useState(0);
  const [loginBar, setLoginBar] = useState('Loading...');

  const formSubmitControl = (event) => {
    login(event);
    setLoginBarState(0);
  }

  const loginButton = () => {
    return (
      <div className="option" onClick={setLoginBarState(1)}>
        Sign In
      </div>)
    }
    

const loginForm = (
  <div className="login-form">
    <form onSubmit={formSubmitControl}>
      <input type="text" name="username"></input>
      <input type="password" name="password"></input>
      <input type="submit" value="Login"></input>
      <input type="button" onClick={() => setLoginBarState(0)} value="Cancel"></input>
    </form>
  </div>
  )

  const logoutButton = (
    <div className="option" onClick={() => logout(getCookieValue('sessionid'))}>
      LogOut
    </div>);
  // const loginBarLogic = () => {
  //   const sessionid_v = getCookieValue('sessionid');    
  //   if (sessionid_v && sessionid_v !== 'undefined') {
  //     if (validateSession(sessionid_v)) {
  //       setLoginBarState(2);
  //     } else {
  //       document.cookie = "";
  //       setLoginBarState(0)
  //     }
  //   } else {
  //     setLoginBarState(0)
  //   }
  // }
  
  // useEffect(() => {

  // }, [loginBarState])

  return (      
    <div className="menu top">
      <div className="title">
        <span>COOK BOOK</span>
      </div>
    <div className="options">
      { loginBarState === 0 ? loginButton : null }
      { loginBarState === 1 ? loginForm : null}
     </div>
    </div>
  )
}

export default TopMenu;
