import axios from 'axios'
import './ContentComponent.css'

const ContentComponent = () => {

  const [Title, setTitle] = useState(null);
  const [Description, setDescription] = useState(null);
  const [Ingredients, setIngredients] = useState(null);
  const [Steps, setSteps] = useState(null)
  const [Files, setFiles] = useState(null)
  
  const fetchElementData = (id) => {
    axios.get(`http://localhost:3001/api/get/recipes/${id}`)
      .then( res => {
        setTitle(res.data.title);
        setDescription(res.data.description);
        setIngredients(res.data.ingredients);
        setSteps(res.data.steps);
        setFiles(res.data.images);
      })
      .catch( err => {
        console.error(err);
        props.editingState(false);
      })
  }

  return (
    <div className="box">
    <div className="recipe title">{Title}</div>
    <div className="recipe image">
      <div className="arrow left" onClick={() => prevPage()}><span>{'<'}</span></div>
        <div className="images">{Images}</div>
      <div className="arrow right" onClick={() => nextPage()}><span>{'>'}</span></div>
    </div>
    <div className="recipe description">
      <h2>Description</h2>
        {Description}
    </div>
    <div className="recipe ingredients">
      <h2>Ingredients</h2>
      <ol>
        {Ingredients}
      </ol>
    </div>
    <div className="recipe steps">
      <h2>Steps</h2>
      {Steps}
    </div>
  </div>
  )
}

export default ContentComponent;
