import React, { useState } from 'react';
import './TopMenu.css';
import Login from '../login.js';
import getCookieValue from '../cookie.js';



const TopMenu = () => {
  const [loging, setLoging] = useState(false)

  

  const loginBarLogic = () => {
    const sessionid_v = getCookieValue('sessionid')
    const loginButton = (
    <div className="option" onClick={() => setLoging(true)}>
      Sign In
    </div>);
    const logoutButton = (
      <div className="option" onClick={() => Login.logout()}>
        LogOut
      </div>);
    const loginForm = (
      <div className="login-form">
        <form onSubmit={Login.login}>
          <input type="text" name="username"></input>
          <input type="password" name="password"></input>
          <input type="submit" value="Login"></input>
          <input type="button" onClick={() => setLoging(false)}></input>
        </form>
      </div>
      )
    if (sessionid_v && sessionid_v !== 'undefined') {
      if (Login.validateSession(sessionid_v)) {
        return logoutButton;
      } else {
        document.cookie = "sessionid=undefined";
        return loging ? loginButton : loginForm;
      }
    } else {
      return loging ? loginButton : loginForm;
    }
  }

  return (      
    <div className="menu top">
      <div className="title">
        <span>COOK BOOK</span>
      </div>
    <div className="options">
      { loginBarLogic() }
     </div>
    </div>
  )
}

export default TopMenu;
