import { useState, useEffect } from 'react'
import MenuElement from './MenuElement/MenuElement.js'
import './MenuComponent.css';

const MenuComponent = ((props) => {

  const [MenuElements, setMenuElements] = useState(['Init Data']);

  // Fetch Menu Elements From DataBase
  const fetchMenuElements = () => {
    const data = [{
      name: 'Data 1'
    }, {
      name: 'Data 2'
    }]
    
    setMenuElements(data);
    // Fetchin Menu Elements
  }

  // Only on page load
  useEffect(() => {
    fetchMenuElements()
  }, [MenuElements])

  return (
    <div className='menu'>
      <span className='text-banner'>Recipe List</span>
      <div className="recipe-list">
        Recipe List Component
      </div>
      {MenuElements.map((element) => {
        return <MenuElement data={element} />
      })}
    </div>
    )
})

export default MenuComponent;
